'use strict'
/** @ignore */ const fs = require('fs')
/** @ignore */ const async = require('async')
/** @ignore */ const Config = require('./models/Config')
/** @ignore */ const Server = require('./models/Server')
/** @ignore */ const Database = require('./models/Database')
/** @ignore */ const Model = require('./models/Model')
/** @ignore */ const Reference = require('./models/Reference')
/** @ignore */ const Middleware = require('./models/Middleware')
/** @ignore */ const Seeder = require('./models/Seeder')
/** @ignore */ const RouteGet = require('./routes/RouteGet')
/** @ignore */ const RoutePost = require('./routes/RoutePost')
/** @ignore */ const RoutePut = require('./routes/RoutePut')
/** @ignore */ const RouteDelete = require('./routes/RouteDelete')

/**
* Se encarga de controlar todo el flujo de la aplicación.
*/
class Insac {

  /**
  * Crea una instancia de la clase Insac.
  * @param {String} [environment='development'] Tipo de ejecución. Valores admitidos: 'development', 'test', 'production'
  * @param {String} [projectPath] - Ruta absoluta del proyecto. Por defecto es la ruta del directorio donde se encuentra el archivo 'package.json'.
  */
  constructor(environment = 'development', projectPath) {

    console.log("\n")
    console.log("|==========================================|")
    console.log("|===   I N S A C    F R A M E W O R K   ===|")
    console.log("|==========================================|")
    console.log("\n")

    /**
    * Instancia del objeto que administra la configuración del proyecto y de la aplicación.
    * @type {Config}
    */
    this.config = new Config({env:environment, projectPath:projectPath})

    /**
    * Lista de modelos.
    * @type {Model[]}
    */
    this.models = []

    /**
    * Lista de middlewares.
    * @type {Middleware[]}
    */
    this.middlewares = []

    /**
    * Lista de rutas.
    * @type {Route[]}
    */
    this.routes = []

    /**
    * Instancia del objeto que administra el servidor.
    * @type {Server}
    */
    this.server = new Server()

    /**
    * Instancia del objeto que administra la base de datos.
    * @type {Database}
    */
    this.database = new Database(this.config.database)
  }

  /**
  * Adiciona un modelo y actualiza las referencias con otros modelos. Si solo se especifica
  *  el nombre, se buscará el archivo que lo define y si no existe tal archivo se creará un
  *  modelo por defecto con este nombre. También es posible enviar una instancia de un modelo
  *  como único argumento
  * @param {!String} name - Nombre del modelo.
  * @param {Object} [model] - Datos del modelo.
  */
  addModel(name, model) {
    if (typeof model == 'undefined') {
      // Si el primer argumento es una instancia de un modelo, organizamos los parámetros y continuamos con la ejecución.
      if (name instanceof Model) {
        model = name
        name = model.name
      } else {
        try {
          // Si no se envía el modelo, buscamos el archivo que define al modelo a partir de su nombre.
          let result = {}
          this._pathModelFile(name, this.config.path.models, result)
          // Si el archivo existe, se carga el modelo desde el archivo.
          if(result.path) {
            let modelFile = require(result.path)
            modelFile(this, this.models)
            return
          }
        } catch(err) { console.log(err); return }
      }
    }
    // 1. Se adiciona el modelo a la colección de modelos.
    this.models[name] = new Model(name, model)
    // 2. Se crea el modelo para realizar consultas con la base de datos.
    let define = this.models[name].sequelize()
    let seqModel = this.database.sequelize.define(define.name, define.attributes, define.options)
    this.database.addModel(seqModel)
    // 3. Por defecto se actualizan las referencias de este modelo.
    this._updateReferences(this.models[name])
  }

  /**
  * Actualiza todas las referencias de un modelo. Si no se especifica el nombre del modelo, se actualizaran las referencias de todos los modelos.
  * @param {Model} model Instancia de un modelo previamente adicionado a la colección de modelos.
  */
  _updateReferences(model) {
    let modelName = model.name
    for (let i in model.fields) {
      let field = model.fields[i]
      if (field instanceof Reference) {
        switch (field.reference.type) {
          case '1:1':
            this.models[field.reference.model].options.associations.push({model:model.name, as:model.options.singular})
            this.database.models[field.reference.model].hasOne(this.database.models[modelName], {as:model.options.singular, foreignKey: field.name})
            this.database.models[modelName].belongsTo(this.database.models[field.reference.model], {as:this.models[field.reference.model].options.singular, foreignKey: field.name})
            break
          case '1:N':
            this.models[field.reference.model].options.associations.push({model:model.name, as:model.options.plurl})
            this.database.models[field.reference.model].hasMany(this.database.models[modelName], {as:model.options.plural, foreignKey: field.name})
            this.database.models[modelName].belongsTo(this.database.models[field.reference.model], {as:this.models[field.reference.model].options.singular, foreignKey: field.name})
            break
          default:
            let msg = `No se reconoce el tipo de referencia '${field.reference.type}' del modelo '${modelName}'`
            throw new Error(msg)
        }
      }
    }
  }

  /**
  * Devuelve la ruta del archivo de un modelo a partir de su nombre, dentro de un determinado directorio, si no lo encuentra devuelve undefined.
  * @param {!String} modelName Nombre del modelo.
  * @param {!String} path Ruta absoluta del directorio de búsqueda.
  * @param {!String} result Objeto que almacenará la ruta del archivo si lo encuentra. Debe pasarse un objeto vacio, despues revisar su contenido.
  * @return {String|undefined}
  */
  _pathModelFile(modelName, path, result) {
    if (fs.statSync(path).isDirectory()) {
      fs.readdirSync(path).forEach(file => {
        let newPath = `${path}/${file}`
        this._pathModelFile(modelName, newPath, result)
      })
    } else {
      let a = path.lastIndexOf('/')
      let b = path.indexOf('.js')
      let c = path.substr(a + 1, b - a - 1)
      if (c == modelName) {
        result.path = path
      }
    }
  }

  /**
  * Adiciona un middleware. Puede cargar un middleware global (name = '/api/auth') o local (name = 'auth')
  * @param {String} name - Nombre del middleware.
  * @param {Function} callback - Funcipon de tipo callback
  */
  addMiddleware(name, callback) {
    if ((typeof name == 'string') && (typeof callback == 'function')) {
      if (name.startsWith('/')) {
        this.server.addMiddleware(name, callback)
      } else {
        this.middlewares[name] = new Middleware(name, callback)
      }
      return
    }

    let msg = `Los parámetros de entrada no son válidos name = '${name}' y callback = '${callback}'`

    if ((typeof name == 'string') && (typeof callback == 'string')) {
      if(!name.startsWith('/')) {
        throw new Error(msg)
      }
      let callbackResult = this._buscarMiddleware(callback)
      if (!callbackResult) {
        throw new Error(`No existe el middleware '${callback}'`)
      }
      if (name.startsWith('/')) {
        this.server.addMiddleware(name, callbackResult.controller)
      } else {
        throw new Warning('El middleware',name,'ha sido sobreescrito')
      }
      return
    }

    throw new Error(msg)
  }

  /** @ignore */
  _buscarMiddleware(name) {
    let fileName = `${this.config.path.middlewares}/${name}.js`
    try {
      let file = require(fileName)
      let db = this.database.models
      db.sequelize = this.database.sequelize
      db.Sequelize = this.database.Sequelize
      try {
        file(this, this.models, db)
      } catch(err) { return undefined }
      return this.middlewares[name]
    } catch (err) {
      if (err.message && err.message.startsWith('Cannot find module')) {
        throw new Error(`No existe el archivo '${fileName}'`)
      }
      console.log(err)
      throw new Error(err)
    }
  }

  /**
  * Adiciona una ruta.
  * @param {String} method - Methodo HTTP.
  * @param {String} path - Ruta de acceso.
  * @param {Object} options - Opciones de creación de la ruta.
  * @throws {Error} Ocurre cuando el método no es válido.
  */
  addRoute(method, path, options) {
    let route
    if (method == 'GET') route = new RouteGet(path, options)
    if (method == 'POST') route = new RoutePost(path, options)
    if (method == 'PUT') route = new RoutePut(path, options)
    if (method == 'DELETE') route = new RouteDelete(path, options)
    if (!route) throw new Error(`El método '${method}' no es válido`)
    this.routes.push(route)
    let db = this.database.models
    db.sequelize = this.database.sequelize
    db.Sequelize = this.database.Sequelize
    this.server.addRoute(route.method, route.path, route.getCallback(this.models, db))
  }

  /**
  * Adiciona todas las rutas que se encuentran dentro del directorio de rutas..
  */
  addRoutes() {
    this._addRoutesFromPath(this.config.path.routes)
  }

  /**
  * Carga todas las rutas que se encuentren dentro de un directorio especifico (path).
  * @param {String} path - Ruta absoluta del directorio donde se encuentran todas las rutas.
  */
  _addRoutesFromPath(path) {
    if (fs.statSync(path).isDirectory()) {
      fs.readdirSync(path).forEach(file => {
        let newPath = `${path}/${file}`
        this._addRoutesFromPath(newPath)
      })
    } else {
      let db = this.database.models
      db.sequelize = this.database.sequelize
      db.Sequelize = this.database.Sequelize
      let routeFile = require(path)
      routeFile(this, this.models, db)
    }
  }

  /**
  * Crea las tablas en la base de datos, a partir de los modelos.
  * @return {Promise<String>} Devuelve una promesa.
  */
  migrate() {
    return this.database.migrate(this.models)
  }

  /**
  * Crea datos por defecto.
  * @return {Promise<String>} Devuelve una promesa.
  */
  seed() {
    let seeder = new Seeder(this.models, this.database)
    return new Promise((resolve, reject) => {
      console.log(" Iniciando seeders ...\n")
      let tasks = []
      try {
        let path = this.config.path.seeders
        fs.readdirSync(path).forEach(file => {
          let seedFile = require(`${path}/${file}`)
          tasks.push(seedFile(seeder))
        })
      } catch (err) {
        return reject(err)
      }
      async.waterfall(tasks, (err, result) => {
        if (err) {
          reject(err)
        } else {
          console.log("\n Seeders finalizado exitosamente")
          resolve('OK')
        }
      })
    })
  }

  /**
  * Inicia la aplicación.
  * @param {Number} port Puerto sobre el que se ejecutará la aplicación. Por defecto es el que se especificó en el archivo de configuración.
  */
  listen(port) {
    if (!port) port = this.config.server.port
    this.server.listen(port)
    console.log(`La aplicación esta ejecutándose en modo '${this.config.env}' sobre el puerto ${port}`)
  }

}

module.exports = Insac

'use strict'

/**
* Describe las caracteristicas y comportamientp de todos los tipos de datos
*/
class DataType {

  /**
  * Crea una instancia de la clase DataType.
  * @param {String} name Nombre que identifica al tipo de dato.
  * @param {Array} args Argumentos necesarios de acuerdo al tipo de dato.
  */
  constructor(name, args) {
    /**
    * Nombre que identifica al tipo de dato.
    * @type {String}
    */
    this.name = name

    /**
    * Argumentos necesarios de acuerdo al tipo de dato.
    * @type {Array}
    */
    this.args = args
  }

  /**
  * Crea un validador por defecto segun el tipo de dato
  * @abstract
  */
  validator() { }

  /**
  * Crea un tipo de dato para crear un modelo sequelize
  * @abstract
  */
  sequelize() { }

}

module.exports = DataType

'use strict'
/** @ignore */ const DataType = require('./DataType')
/** @ignore */ const Sequelize = require('sequelize')
/** @ignore */ const INTEGER_VALIDATOR = require('../validators/INTEGER_VALIDATOR')

/**
* Describe el tipo de dato INTEGER.
* @extends {DataType}
*/
class INTEGER extends DataType {

  /**
  * Crea una instancia de la clase INTEGER.
  */
  constructor() {
    super('INTEGER', [])
  }

  /**
  * Devuelve un validador para el tipo de dato INTEGER.
  * @return {INTEGER_VALIDATOR} instancia de la clase INTEGER_VALIDATOR.
  * @override
  */
  validator() {
    return new INTEGER_VALIDATOR(undefined, undefined)
  }

  /**
  * Devuelve un tipo de dato de tipo INTEGER para crear modelos Sequelize
  * @return {SequelizeType}
  * @override
  */
  sequelize() {
    return Sequelize.INTEGER()
  }

}

module.exports = INTEGER

'use strict'
/** @ignore */ const Sequelize = require('sequelize')
/** @ignore */ const DataType = require('./DataType')
/** @ignore */ const STRING_VALIDATOR = require('../validators/STRING_VALIDATOR')

/**
* Describe el tipo de dato STRING.
* @extends {DataType}
*/
class STRING extends DataType {

  /**
  * Crea una instancia de la clase STRING.
  * @param {Number} [length=255] Longitud máxima de caracteres.
  */
  constructor(length = 255) {
    super('STRING', [length])

    /**
    * Cantidad máxima de caracterres.
    * @type {Number}
    */
    this.length = length
  }

  /**
  * Devuelve un validador para el tipo de dato STRING.
  * @return {STRING_VALIDATOR} instancia de la clase STRING_VALIDATOR.
  * @override
  */
  validator() {
    return new STRING_VALIDATOR(undefined, this.length)
  }

  /**
  * Devuelve un tipo de dato de tipo STRING para crear modelos Sequelize
  * @return {SequelizeType}
  * @override
  */
  sequelize() {
    return Sequelize.STRING(this.length)
  }

}

module.exports = STRING

'use strict'
/** @ignore */ const fs = require('fs')
/** @ignore */ const path = require('path')

/**
* Administra todas las configuraciones del proyecto y de la aplicación.
*/
class Config {

  /**
  * Crea una instancia del objeto Config.
  * @param {Object} [config={}] - Objeto que contiene todas las variables de configuración.
  * @param {String} [config.env='development'] - Tipo de ambiente sobre el que se ejecutará la aplicación (development, test ó production).
  * @param {String} [config.projectPath] - Ruta absoluta del directorio raiz del proyecto.
  * @param {String} [config.path] - Rutas absolutas de los directorios del proyecto.
  * @param {String} [config.path.public] - Carpeta pública.
  * @param {String} [config.path.config] - Carpeta donde se encuentran los archivos de configuración.
  * @param {String} [config.path.global] - Carpeta donde se encuentran los archivos que contienen las variables globales de la aplicación.
  * @param {String} [config.path.middlewares] - Carpeta donde se encuentran los middlewares.
  * @param {String} [config.path.models] - Carpeta donde se encuentran los modelos.
  * @param {String} [config.path.routes] - Carpeta donde se administran todas las rutas.
  * @param {String} [config.path.seeders] - Carpeta donde se encuentran los seeders.
  * @param {Object} [config.server] - Objeto que contiene los campos de configuración del servidor. Todas las opciones posibles se describen en {@link Server#constructor}
  * @param {Object} [config.database] - Configuración del servidor de base de datos. Todas las opciones posibles se describen en {@link Database#constructor}
  * @param {Object} [config.auth] - Objeto que contiene los campos de configuración para el sistema de autenticación.
  * @param {Object} [config.auth.token] - Objeto que contiene información de configuración del token para el sistema de autenticación.
  * @param {String} [config.auth.token.key='SECRET'] - Clave secreta que se utiliza para crear el token.
  * @param {Number} [config.auth.token.expires=86400] - Tiempo en segundos que indica la validez del token desde su creación.
  */
  constructor(config = {}) {
    /**
    * Variable de entorno que almacena el tipo de ejecución.
    * @type {String} [env='development']
    */
    this.env = process.env.NODE_ENV = config.env || 'development'

    /**
    * Ruta del directorio raiz del proyecto.
    * @type {String}
    */
    this.projectPath = config.projectPath || Config.defaultProjectPath()

    /**
    * Rutas de los directorios del proyecto
    * @type {Object}
    */
    this.path = Config.defaultPath(this.projectPath)

    /**
    * Configuración por defecto del servidor
    * @type {Object}
    */
    this.server = Config.defaultServer()

    /**
    * Configuración por defecto de la base de datos
    * @type {Object}
    */
    this.database = Config.defaultDatabase()

    /**
    * Configuración por defecto del sistema de autenticación mediante tokens
    * @type {Object}
    */
    this.auth = Config.defaultAuth()

    // Carga todas las configuraciones personalizadas
    this._loadProjectConfig(config, this.projectPath)
    this._loadAppConfig(config, this.path.config)
    this._loadAppGlobal()
  }

  /**
  * Se actualiza la configuración solamente para el modo de ejecución establecido al momento de crear el objeto. Es posible agregar datos adicionales al archivo de configuración, diferentes a los predefinidos.
  * @param {!String} environment Nombre que identifica al ambiente de ejecución (development, test ó production).
  * @param {Object} [config] Objeto que contiene los campos de configuración. Todas las configuraciones posibles se describen en: {@link Config#constructor}.
  */
  set(environment, config = {}) {
    if (this.env == environment) {
      this._setEnviroment(config)
    }
  }

  /**
  * Carga la configuración del proyecto si es que existe (archivo '.insac.js')
  * @param {Object} config Datos de configuración enviados desde el constructor.
  * @param {String} projectPath Ruta del directorio raiz del proyecto.
  */
  _loadProjectConfig(config = {}, projectPath) {
    try {
      let customConfig = require(path.resolve(projectPath, '.insac.js'))
      // Primero se carga la configuración del archivo .insac.js
      this._setProject(customConfig, projectPath)
    } catch(err) { } finally {
      // Después se carga la configuración que se envía directamente desde el constructor
      this._setProject(config, projectPath)
    }
  }

  /** @ignore  */
  _setProject(config = {}, projectPath) {
    if (config.path) {
      if (config.path.public) this.path.public = path.resolve(projectPath, config.path.public)
      if (config.path.config) this.path.config = path.resolve(projectPath, config.path.config)
      if (config.path.global) this.path.global = path.resolve(projectPath, config.path.global)
      if (config.path.middlewares) this.path.middlewares = path.resolve(projectPath, config.path.middlewares)
      if (config.path.models) this.path.models = path.resolve(projectPath, config.path.models)
      if (config.path.routes) this.path.routes = path.resolve(projectPath, config.path.routes)
      if (config.path.seeders) this.path.seeders = path.resolve(projectPath, config.path.seeders)
    }
  }

  /**
  * Carga la configuración de la aplicación si es que existe (Archivo 'config.js')
  * @param {Object} config Datos de configuración enviados desde el constructor.
  * @param {String} configPath Ruta del directorio Config.
  */
  _loadAppConfig(config = {}, configPath) {
    try {
      // Primero carga la configuración del archivo config.js
      this._loadAppConfigFromPath(configPath)
    } catch(err) { } finally {
      // Después se carga la configuración que se envía directamente desde el constructor
      this._setEnviroment(config)
    }
  }

  /** @ignore */
  _loadAppConfigFromPath(path) {
    if (fs.statSync(path).isDirectory()) {
      fs.readdirSync(path).forEach(file => {
        let newPath = `${path}/${file}`
        this._loadAppConfigFromPath(newPath)
      })
    } else {
      try { require(path)(this) } catch(err) { }
    }
  }

  /**
  * Carga todas las variables globales de la aplicación.
  */
  _loadAppGlobal() {
    try {
      this._loadAppGlobalFromPath(this.path.global)
    } catch(err) { }
  }

  /** @ignore */
  _loadAppGlobalFromPath(path) {
    if (fs.statSync(path).isDirectory()) {
      fs.readdirSync(path).forEach(file => {
        let newPath = `${path}/${file}`
        this._loadAppGlobalFromPath(newPath)
      })
    } else {
      require(path)
    }
  }

  /** @ignore */
  _setEnviroment(config = {}) {
    for (let prop in config) {
      if ((prop == 'env') || (prop == 'projectPath') || (prop == 'server') || (prop == 'database') || (prop == 'auth')) {
        if (prop == 'server') {
          if (config.server.all200) this.server.all200 = config.server.all200
          if (config.server.port) this.server.port = config.server.port
        }
        if (prop == 'database') {
          if (config.database.name) this.database.name = config.database.name
          if (config.database.username) this.database.username = config.database.username
          if (config.database.password) this.database.password = config.database.password
          if (config.database.dialect) this.database.dialect = config.database.dialect
          if (config.database.host) this.database.host = config.database.host
          if (config.database.port) this.database.port = config.database.port
        }
        if (prop == 'auth') {
          if (config.auth.token) {
            if (config.auth.token.key) this.auth.token.key = config.auth.token.key
            if (config.auth.token.expires) this.auth.token.expires = config.auth.token.expires
          }
        }
        continue
      }
      /**
      * Información extra que puede adicionarse al archivo de configuración.
      * @type {Object} prop
      */
      this[prop] = config[prop]
    }
  }

  /**
  * Directorio por defecto del proyecto
  * @return {String}
  */
  static defaultProjectPath(projectPath) {
    return path.resolve(__dirname, './../../../../')
  }

  /**
  * Configuración por defecto de las carpetas del proyecto.
  * @param {String} projectPath - Directorio base (directorio raiz del proyecto).
  * @return {Object}
  */
  static defaultPath(projectPath) {
    return {
      public: path.resolve(projectPath, './public'),
      config: path.resolve(projectPath, './src/config'),
      global: path.resolve(projectPath, './src/global'),
      middlewares: path.resolve(projectPath, './src/middlewares'),
      models: path.resolve(projectPath, './src/models'),
      routes: path.resolve(projectPath, './src/routes'),
      seeders: path.resolve(projectPath, './src/seeders')
    }
  }

  /**
  * Configuración por defecto del servidor.
  * @return {Object}
  */
  static defaultServer() {
    return {
      port: 3200,
      all200: false
    }
  }

  /**
  * Configuración por defecto de la base de datos
  * @return {Object}
  */
  static defaultDatabase() {
    return {
      name: 'insac_app',
      username: 'postgres',
      password: 'postgres',
      dialect: 'postgres',
      host: 'localhost',
      port: 5432
    }
  }

  /**
  * Configuración por defecto del sistema de autenticación mediante tokens
  * @return {Object}
  */
  static defaultAuth() {
    return {
      token: {
        key: 'SECRET',
        expires: 86400
      }
    }
  }

}

module.exports = Config

'use strict'
/** @ignore */ const Sequelize = require('sequelize')
/** @ignore */ const async = require('async')
/** @ignore */ const Config = require('./Config')

/**
* Se encarga de controlar la base de datos.
*/
class Database {

  /**
  * Crea una instancia de la clase Database.
  * @param {Object} [config] - Opciones de configuración del servidor de base de datos.
  * @param {String} [config.name='insac_app'] - Nombre de la base de datos.
  * @param {String} [config.username='postgres'] - Nombre del usuario.
  * @param {String} [config.password='postgres'] - Contraseña del usuario.
  * @param {String} [config.dialect='postgres'] - Gestor de base de datos (postgres ó mysql).
  * @param {String} [config.host='localhost'] - Nombre del servidor de base de datos.
  * @param {Number} [config.port=5432] - Puerto sobre el que se ejecuta el servicio de base de datos.
  */
  constructor(config = Config.defaultDatabase()) {
    let name = config.name || 'insac_app'
    let username = config.username || 'postgres'
    let password = config.password || 'postgres'
    let options = {
      dialect: config.dialect || 'postgres',
      host: config.host || 'localhost',
      port: config.port || 5432,
      logging: false
    }
    /**
    * Instancia de la clase async
    * @type {Sequelize}
    * @see http://docs.sequelizejs.com/
    */
    this.sequelize = new Sequelize(name, username, password, options)

    /**
    * Lista de todos los modelos de sequelze para realizar consultas.
    * @type {SequelizeModel[]}
    */
    this.models = []
  }

  /**
  * Adiciona un modelo
  * @param {SequelizeModel} model Modelo sequelize
  */
  addModel(model) {
    this.models[model.name] = model
  }

  /**
  * Crea las tablas de la base de datos.
  * @param {Model[]} models Modelos de la aplicación.
  * @return {Promise}
  */
  migrate(models) {
    console.log("Iniciando migración ...")
    let seq = this.sequelize

    let migratePromise = new Promise((resolve, reject) => {
      let options = {cascade:true}
      let queryInterface = seq.queryInterface

      seq.drop(options).done(result => {
        console.log("\n · Todas las tablas han sido eliminadas\n")
        let asyncFunctions = []

        for (let i in models) {
          let model = models[i]

          let define = model.sequelize()
          let tableName = define.name, attributes = define.attributes, options = define.options
          /*if (model.options.timestamps) {
            attributes[model.options.createdAt] = {type: Sequelize.DATE}
            attributes[model.options.updatedAt] = {type: Sequelize.DATE}
          }*/
          asyncFunctions.push(function(callback) {
            queryInterface.createTable(tableName, attributes, options).done(result => {
              console.log(` - Se creó la tabla '${tableName}'`)
              callback(null)
            })
          })
        }

        async.waterfall(asyncFunctions, function(err, result) {
          if (err) {
            reject(new Error(err))
          }
          console.log(`\nMigración finalizada\n`)
          resolve()
        })

      })

    })

    return migratePromise
  }


}

module.exports = Database

'use strict'
/** @ignore */ const DataTypes = require('../tools/DataTypes')

/**
* Describe las caracteristicas de un campo de un modelo.
*/
class Field {

  /**
  * Crea una instancia de la clase Field. El método recibe dos argumentos, pero es posible enviar solamente uno el cual se reonocerá como el parámetro field.
  * @param {String} [name='field'] Nombre del campo.
  * @param {Object} [field={}] Caracteristicas del campo.
  * @param {DataType} [field.type=STRING] Tipo de dato del campo.
  * @param {Validator} [field.validator] Objeto que se encarga de la validación del campo. Por defecto se asigna un validador de acuerdo al tipo de dato
  * @param {String} [field.description=undefined] Descripción del campo.
  * @param {Boolean} [field.allowNull=true] Indica si el campo acepta valores nulos.
  * @param {Boolean} [field.primaryKey=false] Indica si el campo es una llave primaria.
  * @param {Boolean} [field.autoIncrement=false] Indica si es un valor autoincrementable.
  * @param {String|Number|Boolean} [field.defaultValue=undefined] Valor por defecto del campo.
  */
  constructor(name, field) {
    // Se actualizan los parámetros de entrada en base al numero de argmunetos recibidos
    if (arguments.length <= 1) {
      name = 'field'
      field = (arguments.length == 1) ? arguments[0] : {}
    }

    /**
    * Nombre del campo.
    * @type {String}
    */
    this.name = name

    /**
    * Tipo de dato del campo.
    * @type {DataType}
    */
    this.type = field.type || DataTypes.STRING()

    /**
    * Objeto que se encarga de la validación del campo.
    * @type {Validator}
    */
    this.validator = field.validator || this.type.validator()

    /**
    * Descripción del campo.
    * @type {String}
    */
    this.description = field.description || undefined

    /**
    * Indica si el campo acepta valores nulos.
    * @type {Boolean}
    */
    this.allowNull = (typeof field.allowNull != 'undefined') ? field.allowNull : true

    /**
    * Indica si el campo es una llave primaria.
    * @type {Boolean}
    */
    this.primaryKey = (typeof field.primaryKey != 'undefined') ? field.primaryKey : false

    /**
    * Indica si es un valor autoincrementable.
    * @type {Boolean}
    */
    this.autoIncrement = (typeof field.autoIncrement != 'undefined') ? field.autoIncrement : false

    /**
    * Valor por defecto del campo.
    * @type {String|Number|Boolean}
    */
    this.defaultValue = field.defaultValue || undefined
  }

  /**
  * Devuelve un objeto para el campo de un modelo sequelize (SequelizeModel)
  * @return {Object} Campo de un modelo sequelize (SequelizeModel).
  */
  sequelize() {
    let attribute = {
      type: this.type.sequelize()
    }
    if (typeof this.allowNull != 'undefined') attribute.allowNull = this.allowNull
    if (typeof this.primaryKey != 'undefined') attribute.primaryKey = this.primaryKey
    if (typeof this.autoIncrement != 'undefined') attribute.autoIncrement = this.autoIncrement
    if (typeof this.defaultValue != 'undefined') attribute.defaultValue = this.defaultValue
    return attribute
  }

  /**
  * Devuelve un campo de tipo ID
  * @return {Field}
  */
  static ID() {
    return new Field('id', {
      type: DataTypes.INTEGER(),
      description: 'Identificador único',
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    })
  }

}

Field.THIS = '__this__'

module.exports = Field

'use strict'
/** @ignore */ const cors = require('cors');
/** @ignore */ const bodyParser = require('body-parser');

/**
* Describe las caracteristicas y comportamiento de un middleware.
*/
class Middleware {

  /**
  * Crea una instancia de la clase Middleware.
  * @param {!String} name - Nombre con el que se identificará al middleware.
  * @param {!Function} controller - Función de tipo callback.
  */
  constructor(name, controller) {
    /**
    * Nombre con el que se identificará al middleware.
    * @type {String}
    */
    this.name = name

    /**
    * Función de tipo callback
    * @type {String}
    */
    this.controller = controller
  }

  /**
  * Devuelve un middleware para controlar el Intercambio de Recursos de Origen Cruzado (CORS, por sus siglas en inglés).
  * @see https://www.npmjs.com/package/cors
  * @return {Middleware}
  */
  static CORS() {
    return new Middleware('cors', cors({
      "origin": "*",
      "methods": "GET,POST,PUT,PATCH,DELETE,OPTIONS",
      "preflightContinue": false,
      "allowedHeaders": "Content-Type,Authorization,Content-Length,Content-Range"
    }))
  }

  /**
  * Devuelve un middleware que solo parsea el contenido de la variable body como JSON.
  * @see https://www.npmjs.com/package/body-parser
  * @return {Middleware}
  */
  static BODY_PARSER_JSON() {
    return new Middleware('body-parser-json', bodyParser.json())
  }

  /**
  * Devuelve un middleware que parsea la url, para utilizar la variable req.body.
  * @see https://www.npmjs.com/package/body-parser
  * @return {Middleware}
  */
  static BODY_PARSER_URL_ENCODED() {
    return new Middleware('body-parser-url-encoded', bodyParser.urlencoded({ extended: false }))
  }

}

