/**
* Se encarga de controlar el servidor sobre el que se ejecuta la aplicación.
*/
class Server {

  /**
  * Crea una instancia de la clase Server.
  * @param {Object} [config] Opciones de configuración del servidor.
  * @param {Number} [config.port=3200] - Puerto sobre el que se ejecutará la aplicación.
  * @param {Boolean} [config.all200=false] - Indica si todos los códigos de respuesta del servidor seran 200 (status: OK). El código de error verdadero se incluirá en el body.
  */
  constructor(config = Config.defaultServer()) {
    /**
    * Instancia del servidor express
    * @type {ExpressServer}
    * @see http://expressjs.com/es/
    */
    this.express = express()

    let response = new Response(config)
    response.assignTo(this.express.response)

    // Carga los middlewares por defecto
    this._loadDefaultMiddlewares()
  }

  /**
  * Incorpora un middleware global al servidor.
  * @param {String} path Rutas a las que afectará el middleware.
  * @param {Function} callback Función de tipo callback.
  */
  addMiddleware(path, callback) {
    console.log(" [MIDDLEWARE] ", path)
    this.express.use(path, callback)
  }

  /**
  * Incorpora una ruta al servidor.
  * @param {String} method - Methodo HTTP.
  * @param {String} path - Ruta de acceso.
  * @param {Function} callback Función de tipo callback.
  */
  addRoute(method, path, callback) {
    this.express[method.toLowerCase()](path, callback)
    console.log(` [${method}] ${path}`)
  }

  /**
  * Inicia el servidor para que acepte peticiones del cliente. Devuelve una función de tipo Promise.
  * @param {Number} [port] Puerto sobre el que se ejecutarrá la aplicación.
  */
  listen(port) {
    this.express.listen(port)
  }

  /**
  * Incorpora los middlewares por defecto al servidor
  */
  _loadDefaultMiddlewares() {
    this.express.use(Middleware.CORS().controller)
    this.express.use(Middleware.BODY_PARSER_JSON().controller)
    this.express.use(Middleware.BODY_PARSER_URL_ENCODED().controller)
  }
}

module.exports = Server

'use strict'
/** @ignore */ const fs = require('fs')

/**
* Describe las caracteristicas y comportamiento de una ruta.
*/
class Route {

  /**
  * Crea una instancia de la clase Route
  * @param {String} method Método HTML que manejará la ruta.
  * @param {String} path URI del recurso.
  * @param {Object} options Opciones de la ruta.
  * @param {String} options.model Módelo asociado a la ruta.
  * @param {Number} [options.version=1] Número de versión de la ruta..
  * @param {Object} [options.req={}] Descripción de los datos de entrada.
  * @param {Object} [options.req.headers] Datos provenientes del encabezado (header).
  * @param {Object} [options.req.params] Datos provenientes de la URI.
  * @param {Object} [options.req.body] Datos Datos provenientes del cuerpo (body).
  * @param {Object} [options.res={}] Descripción de los datos de salida.
  * @param {Object} [options.res.data] Formato de os datos de salida.
  * @param {Function} [options.controller] Función de tipo callback que describe el comportamiento de la ruta.
  */
  constructor(method, path, options = {}) {
    /**
    * Método HTML.
    * @type {String}
    */
    this.method = method

    /**
    * URI del recurso.
    * @type {String}
    */
    this.path = path

    /**
    * Modelo asociado a esta ruta.
    * @type {Model}
    */
    this.model = options.model

    /**
    * Versión de la ruta
    * @type {Number}
    */
    this.version = options.version || 1

    /**
    * Datos de entrada
    * @type {Object}
    */
    this.req = {}
    if (options.req) {
      this.req = {
        headers: options.req.headers || {},
        params: options.req.params || {},
        body: options.req.body || {}
      }
    }

    /**
    * Datos de salida
    * @type {Object}
    */
    this.res = {
      data: {}
    }
    if (options.res) {
      this.res = {
        data: options.res.data
      }
    }
    if (!options.res && method == 'POST') {
      this.res = {
        data: this.model.fields
      }
    }

    /**
    * Controlador de la ruta.
    * @type {Function}
    */
    this.controller = options.controller
  }

  /**
  * Devuelve una función de tipo callback que describe todo el comportamiento de la ruta.
  * @param {Model[]} models Modelos de la aplicación.
  * @param {Object} db Objeto que contiene todos los modelos sequelize, una instancia y una referencia de la clase Sequelize.
  * @abstract
  */
  getCallback(models, db) {  }

}

module.exports = Route

